glsl3.vert:
precision highp float;
uniform CCGlobal {
  vec4 cc_time;

  vec4 cc_screenSize;

  vec4 cc_screenScale;

  vec4 cc_nativeSize;

  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;

  vec4 cc_exposure;

  vec4 cc_mainLitDir;

  vec4 cc_mainLitColor;

  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};

in vec3 a_position;

in mediump vec2 a_uv0;
out mediump vec2 v_uv0;

in vec4 a_color;
out vec4 v_color;

void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  v_uv0 = a_uv0;
  v_color = a_color;
}

glsl3.frag:
precision highp float;

uniform sampler2D texture;
in mediump vec2 v_uv0;
in vec4 v_color;

void main () {
  vec4 color = v_color * texture2D(texture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_TEXTURE
      color.a *= texture2D(texture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  float gray = 0.2126*color.r + 0.7152*color.g + 0.0722*color.b;
  gl_FragColor = vec4(gray, gray, gray, color.a);
}

