glsl3.vert:
precision highp float;
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
uniform CCGlobal {
  vec4 cc_time;

  vec4 cc_screenSize;

  vec4 cc_screenScale;

  vec4 cc_nativeSize;

  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;

  vec4 cc_exposure;

  vec4 cc_mainLitDir;

  vec4 cc_mainLitColor;

  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};

#if CC_USE_SKINNING

  in vec4 a_weights;
  in vec4 a_joints;

  #if CC_USE_JOINTS_TEXTRUE
    uniform SKINNING {
      vec2 cc_jointsTextureSize;
    }
    uniform sampler2D cc_jointsTexture;

    #if CC_JOINTS_TEXTURE_FLOAT32
      mat4 getBoneMatrix(const in float i) {
        float width = cc_jointsTextureSize.x;
        float height = cc_jointsTextureSize.y;
        float j = i * 4.0;
        float x = mod(j, width);
        float y = floor(j / width);

        float dx = 1.0 / width;
        float dy = 1.0 / height;

        y = dy * (y + 0.5);

        vec4 v1 = texture2D(cc_jointsTexture, vec2(dx * (x + 0.5), y));
        vec4 v2 = texture2D(cc_jointsTexture, vec2(dx * (x + 1.5), y));
        vec4 v3 = texture2D(cc_jointsTexture, vec2(dx * (x + 2.5), y));
        vec4 v4 = texture2D(cc_jointsTexture, vec2(dx * (x + 3.5), y));

        return mat4(v1, v2, v3, v4);
      }
    #else
      float decode32(vec4 rgba) {
        float Sign = 1.0 - step(128.0, rgba[0]) * 2.0;
        float Exponent = 2.0 * mod(rgba[0], 128.0) + step(128.0, rgba[1]) - 127.0;
        float Mantissa = mod(rgba[1], 128.0) * 65536.0 + rgba[2] * 256.0 + rgba[3] + 8388608.0;
        return Sign * exp2(Exponent - 23.0) * Mantissa;
      }
      vec4 decodevec4 (vec4 x, vec4 y, vec4 z, vec4 w) {

        return vec4(
          decode32(x.wzyx * 255.0),
          decode32(y.wzyx * 255.0),
          decode32(z.wzyx * 255.0),
          decode32(w.wzyx * 255.0)
        );
      }

      vec4 decodevec4 (float dx, float x, float y) {
        return decodevec4(
          texture2D(cc_jointsTexture, vec2(dx * (x + 0.5), y)),
          texture2D(cc_jointsTexture, vec2(dx * (x + 1.5), y)),
          texture2D(cc_jointsTexture, vec2(dx * (x + 2.5), y)),
          texture2D(cc_jointsTexture, vec2(dx * (x + 3.5), y))
        );
      }

      mat4 getBoneMatrix(const in float i) {
        float width = cc_jointsTextureSize.x;
        float height = cc_jointsTextureSize.y;
        float j = i * 16.0;
        float x = mod(j, width);
        float y = floor(j / width);

        float dx = 1.0 / width;
        float dy = 1.0 / height;

        y = dy * (y + 0.5);

        vec4 v1 = decodevec4(dx, x,       y);
        vec4 v2 = decodevec4(dx, x+4.0,   y);
        vec4 v3 = decodevec4(dx, x+8.0,   y);
        vec4 v4 = decodevec4(dx, x+12.0,  y);

        return mat4(v1, v2, v3, v4);
      }
    #endif
  #else
    uniform CC_JOINT_MATRIX {
      mat4 cc_jointMatrices[50];
    }

    mat4 getBoneMatrix(const in float i) {
      return cc_jointMatrices[int(i)];
    }
  #endif

    mat4 skinMatrix() {
      return
        getBoneMatrix(a_joints.x) * a_weights.x +
        getBoneMatrix(a_joints.y) * a_weights.y +
        getBoneMatrix(a_joints.z) * a_weights.z +
        getBoneMatrix(a_joints.w) * a_weights.w
        ;
    }
#endif

void SKIN_VERTEX(inout vec4 a1) {
  #if CC_USE_SKINNING
    mat4 m = skinMatrix();
    a1 = m * a1;
  #endif
}

void SKIN_VERTEX(inout vec4 a1, inout vec4 a2) {
  #if CC_USE_SKINNING
    mat4 m = skinMatrix();
    a1 = m * a1;
    a2 = m * a2;
  #endif
}

void SKIN_VERTEX(inout vec4 a1, inout vec4 a2, inout vec4 a3) {
  #if CC_USE_SKINNING
    mat4 m = skinMatrix();
    a1 = m * a1;
    a2 = m * a2;
    a3 = m * a3;
  #endif
}

uniform MAIN_TILING {
  vec2 mainTiling;
  vec2 mainOffset;
}

in vec3 a_position;

#if CC_USE_ATTRIBUTE_UV0 && USE_DIFFUSE_TEXTURE
  in mediump vec2 a_uv0;
  out mediump vec2 v_uv0;
#endif

#if CC_USE_ATTRIBUTE_COLOR
  in lowp vec4 a_color;
  out lowp vec4 v_color;
#endif

void main () {
  vec4 position = vec4(a_position, 1);

  SKIN_VERTEX(position);

  #if CC_USE_ATTRIBUTE_COLOR
      v_color = a_color;
  #endif

  #if CC_USE_ATTRIBUTE_UV0 && USE_DIFFUSE_TEXTURE
      v_uv0 = a_uv0 * mainTiling + mainOffset;
  #endif

  gl_Position = cc_matViewProj * cc_matWorld * position;
}

glsl3.frag:
precision highp float;

vec3 SRGBToLinear(vec3 gamma)
{
	return pow(gamma, vec3(2.2));

}

vec3 LinearToSRGB(vec3 linear)
{
	return pow(linear, vec3(0.454545));

}

vec4 TEXEL_TO_LINEAR (in vec4 color) {
  #if INPUT_IS_GAMMA
    return vec4(SRGBToLinear(color.rgb), color.a);
  #else
    return color;
  #endif
}

vec4 LINEAR_TO_OUTPUT_TEXEL (in vec4 color) {
  #if OUTPUT_TO_GAMMA
    return vec4(LinearToSRGB(color.rgb), color.a);
  #else
    return color;
  #endif
}

#if USE_ALPHA_TEST

  uniform ALPHA_TEST {
    float alphaThreshold;
  }
#endif

void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}

void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}

uniform DIFFUSE {
  lowp vec4 diffuseColor;
};

#if USE_DIFFUSE_TEXTURE
  uniform sampler2D diffuseTexture;
#endif

void MULTIPLY_DIFFUSE_TEXTRUE_COLOR (inout vec4 color, in vec2 uv) {
  #if USE_DIFFUSE_TEXTURE && CC_USE_ATTRIBUTE_UV0
    vec4 diffuseTextureColor = texture2D(diffuseTexture, uv);
    #if CC_USE_ALPHA_ATLAS_DIFFUSETEXTURE
      diffuseTextureColor.a *= texture2D(diffuseTexture, uv + vec2(0, 0.5)).r;
    #endif
    color *= TEXEL_TO_LINEAR(diffuseTextureColor);
  #endif
}

void CALC_DIFFUSE (inout vec4 color, in vec2 uv) {
  color *= diffuseColor;
  MULTIPLY_DIFFUSE_TEXTRUE_COLOR(color, uv);
}

void CALC_DIFFUSE (inout vec4 color) {
  color *= diffuseColor;
}

#if CC_USE_ATTRIBUTE_COLOR
  in lowp vec4 v_color;
#endif

#if CC_USE_ATTRIBUTE_UV0 && USE_DIFFUSE_TEXTURE
  in mediump vec2 v_uv0;
#endif

void main () {
  vec4 color = vec4(1, 1, 1, 1);

  #if CC_USE_ATTRIBUTE_UV0 && USE_DIFFUSE_TEXTURE
    CALC_DIFFUSE(color, v_uv0);
  #else 
    CALC_DIFFUSE(color);
  #endif

  #if CC_USE_ATTRIBUTE_COLOR
    color *= v_color;
  #endif

  ALPHA_TEST(color);

  gl_FragColor = LINEAR_TO_OUTPUT_TEXEL( color );
}

